          let textBefore = "";
          let pos = view.state.selection.$from.pos;
          let node = view.state.doc.nodeAt(pos);
          while (node && node.type.name === "text") {
            textBefore = node.text + textBefore;
            pos -= node.nodeSize;
            node = view.state.doc.nodeAt(pos);
          }

          // Check if the word before the space is the first word in a sentence
          if (textBefore.match(/[.!?]$/) || pos === 1 || view.state.doc.nodeAt(pos - 2).type.name === "paragraph") {
            // Create a new transaction that inserts the uppercase version of the first character in the word
            const transaction = view.state.tr.insertText(textBefore.replace(/^(\S)/, (match, firstChar) => firstChar.toUpperCase()));

            // Dispatch the transaction
            view.dispatch(transaction);

            // Stop the default insertion behavior
            return true;
          }
        }

        // If the inserted character is not a space, or if the word before the space is not the first word in a sentence,
        // allow the default insertion behavior to continue
        return false;
      },
    },
  });
}
